import { Component, OnInit, Inject, Renderer2, ElementRef, ViewChild, HostListener } from '@angular/core';
import { Router, NavigationEnd, ActivatedRoute } from '@angular/router'; // Added ActivatedRoute
import { Title } from '@angular/platform-browser'; // Added Title service
import { DOCUMENT } from '@angular/common';
import { Location } from '@angular/common';
import { NavbarComponent } from './shared/navbar/navbar.component';
import { filter, map, Subscription } from 'rxjs'; // Added map

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
    private _router: Subscription;
    @ViewChild(NavbarComponent) navbar: NavbarComponent;

    constructor(
        private renderer : Renderer2, 
        private router: Router, 
        @Inject(DOCUMENT,) private document: any, 
        private element : ElementRef, 
        public location: Location,
        private titleService: Title, // Inject Title
        private activatedRoute: ActivatedRoute // Inject ActivatedRoute
    ) {}

    ngOnInit() {
        // --- SEO TITLE LOGIC ---
        this.router.events.pipe(
            filter(event => event instanceof NavigationEnd),
            map(() => {
                let child = this.activatedRoute.firstChild;
                while (child.firstChild) {
                    child = child.firstChild;
                }
                if (child.snapshot.data['title']) {
                    return child.snapshot.data['title'];
                }
                return 'Spirecrest Solutions'; // Fallback Title
            })
        ).subscribe((title: string) => {
            this.titleService.setTitle(title);
        });

        // --- NAVBAR LOGIC ---
        var navbar : HTMLElement = this.element.nativeElement.children[0].children[0];
        this._router = this.router.events.pipe(filter(event => event instanceof NavigationEnd)).subscribe((event: NavigationEnd) => {
            if (window.outerWidth > 991) {
                window.document.children[0].scrollTop = 0;
            }else{
                window.document.activeElement.scrollTop = 0;
            }
            this.navbar.sidebarClose();

            this.renderer.listen('window', 'scroll', (event) => {
                const number = window.scrollY;
                var _location = this.location.path();
                _location = _location.split('/')[2];

                if (number > 150 || window.pageYOffset > 150) {
                    navbar.classList.remove('navbar-transparent');
                } else if (_location !== 'login' && this.location.path() !== '/nucleoicons') {
                    // remove logic
                    navbar.classList.add('navbar-transparent');
                }
            });
        });
    }

    // --- SECURITY PROTOCOLS ---

    // 1. Disable Right Click Globally
    @HostListener('document:contextmenu', ['$event'])
    onRightClick(event: MouseEvent) {
        event.preventDefault();
    }

    // 2. Disable Drag and Drop (Prevents dragging images to desktop)
    @HostListener('document:dragstart', ['$event'])
    onDragStart(event: DragEvent) {
        event.preventDefault();
    }

    // 3. Disable Keyboard Shortcuts (F12, Ctrl+Shift+I, Ctrl+U, PrintScreen)
    @HostListener('document:keydown', ['$event'])
    handleKeyboardEvent(event: KeyboardEvent) {
        // F12
        if (event.key === 'F12') {
            event.preventDefault();
            return false;
        }
        // Ctrl+Shift+I (Inspect), Ctrl+Shift+J (Console), Ctrl+U (Source)
        if (event.ctrlKey && event.shiftKey && (event.key === 'I' || event.key === 'J' || event.key === 'C')) {
            event.preventDefault();
            return false;
        }
        if (event.ctrlKey && event.key === 'u') {
            event.preventDefault();
            return false;
        }
    }
}

